sys_script_client.script:
// Client Script - onSubmit Function
function onSubmit() {
    var username = g_form.getValue('username');  // Get the username from the form field
    var displayName = g_form.getValue('display_name');  // Get the display name
    var email = g_form.getValue('email');  // Get the email address

    // Prepare the data to be sent to the Scripted REST API
    var requestBody = {
        "userName": username,
        "displayName": displayName,
        "emails": [
            { "value": email }
        ]
    };

    // Create GlideAjax object and call the Scripted REST API
    var ga = new GlideAjax('SendUserData'); // Use the name of the Script Include that calls the Scripted REST API
    ga.addParam('userData', JSON.stringify(requestBody)); // Send the JSON data

    // Make the call to the Scripted REST API
    ga.getXMLAnswer(function(response) {
        var responseData = JSON.parse(response.responseText);

        if (responseData.success) {
            // Handle successful response
            alert('User created successfully!');
        } else {
            // Handle error response
            alert('Error creating user: ' + responseData.error);
        }
    });

    // Return false to prevent form submission if you're handling submission via Script
    return false;
}
